IF NOT EXISTS (SELECT 1 FROM ConfigurationVariables WHERE Name='LoginValidationStringForWeb')
BEGIN
	INSERT INTO ConfigurationVariables(Name, Value, Description) VALUES ('LoginValidationStringForWeb', '(^((([a-z]|\d|[!#\$%''\*\+\-\/=\?\^_`{\|}~]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])+(\.([a-z]|\d|[!#\$%''\*\+\-\/=\?\^_`{\|}~]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])+)*)|((\x22)((((\x20|\x09)*(\x0d\x0a))?(\x20|\x09)+)?(([\x01-\x08\x0b\x0c\x0e-\x1f\x7f]|\x21|[\x23-\x5b]|[\x5d-\x7e]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(\\([\x01-\x09\x0b\x0c\x0d-\x7f]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF]))))*(((\x20|\x09)*(\x0d\x0a))?(\x20|\x09)+)?(\x22)))@((([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])*([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])))\.)+(([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])*([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])))\.?$)', 'Regular expression for login validity')
END
GO
IF NOT EXISTS (SELECT 1 FROM ConfigurationVariables WHERE Name='NumOfInvalidPasswordAttempts')
BEGIN
	INSERT INTO ConfigurationVariables(Name, Value, Description) VALUES ('NumOfInvalidPasswordAttempts', 3, 'Max number of wrong password attempts')
END
GO
IF NOT EXISTS (SELECT 1 FROM ConfigurationVariables WHERE Name='InvalidPasswordAttemptsPeriodSeconds')
BEGIN
	INSERT INTO ConfigurationVariables(Name, Value, Description) VALUES ('InvalidPasswordAttemptsPeriodSeconds', 30, 'Time period in which password attempts are counted')
END
GO
IF NOT EXISTS (SELECT 1 FROM ConfigurationVariables WHERE Name='InvalidPasswordBlockSeconds')
BEGIN
	INSERT INTO ConfigurationVariables(Name, Value, Description) VALUES ('InvalidPasswordBlockSeconds', 30, 'Number of seconds to block password')
END
GO
IF NOT EXISTS (SELECT 1 FROM ConfigurationVariables WHERE Name='PasswordValidity')
BEGIN
	INSERT INTO ConfigurationVariables(Name, Value, Description) VALUES ('PasswordValidity', '(^.{6,}$)', 'Regular expression to validate password validity')
END
GO
IF NOT EXISTS (SELECT 1 FROM ConfigurationVariables WHERE Name='LoginValidity')
BEGIN
	INSERT INTO ConfigurationVariables(Name, Value, Description) VALUES ('LoginValidity', '(^[.\-@_a-zA-Z0-9\\]{1,40}$)|(^((([a-z]|\d|[!#\$%''\*\+\-\/=\?\^_`{\|}~]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])+(\.([a-z]|\d|[!#\$%''\*\+\-\/=\?\^_`{\|}~]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])+)*)|((\x22)((((\x20|\x09)*(\x0d\x0a))?(\x20|\x09)+)?(([\x01-\x08\x0b\x0c\x0e-\x1f\x7f]|\x21|[\x23-\x5b]|[\x5d-\x7e]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(\\([\x01-\x09\x0b\x0c\x0d-\x7f]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF]))))*(((\x20|\x09)*(\x0d\x0a))?(\x20|\x09)+)?(\x22)))@((([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])*([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])))\.)+(([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])*([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])))\.?$)', 'Regular expression to validate login validity')
END
GO
